/*Table structure for table `author` */

DROP TABLE IF EXISTS `author`;

CREATE TABLE `author` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `age` int(11) DEFAULT NULL,
  `sex` int(11) DEFAULT NULL,
  `introduce` text CHARACTER SET utf8 COLLATE utf8_general_ci,
  `qq` varchar(255) DEFAULT NULL,
  `webo` varchar(255) DEFAULT NULL,
  `email` varchar(255) DEFAULT NULL,
  `avtor` varchar(255) DEFAULT NULL,
  `service` text,
  `phone` varchar(16) DEFAULT NULL,
  `wechat` varchar(100) DEFAULT NULL,
  `qrcode` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

/*Data for the table `author` */

insert  into `author`(`id`,`name`,`age`,`sex`,`introduce`,`qq`,`webo`,`email`,`avtor`,`service`,`phone`,`wechat`,`qrcode`) values (7,'111',12,1,'叶长亮，10年SEO实战经验，涉及多个行业的关键词优化，比如医疗、机械、电子、家装等等，优化案例众多，经得起检验。多年来，我把每一个SEO客户都看成是自己的朋友，努力帮他们做好SEO工作，为很多企业实现互联网腾飞做出贡献！\r\n\r\n这是一个苹果掉下来砸住牛顿的时代，只是很多人对苹果的价值视而不见。于是，在电商崭露头角的时候，他们把大把的钞票给了马云，在微商异军突起的时候，他们把钱包丢向了马化腾。其实，机会是平等的，平庸的人总在抱怨，他们与乔布斯捧着相同的苹果，却不知加以利用。在IT的世界，一夜暴富已经不是神话，可是一个人不懂电商、不懂微商、不懂SEO、不懂网络营销，苹果砸到头上却捕捉不到商机，最后只能浩叹。\r\n\r\n叶长亮立足于网站推广、SEO技术咨询，旨在为有理想、敢拼搏、不甘平庸要在IT世界里淘金子的人服务，帮助他们捕捉商机，做好推广工作。一个不想做精英的草根不是好草根，叶长亮给你一片清辉，与你风雨同舟。\r\n\r\n\r\n原文地址：http://seo.yechangliang.com/about.html','222','222','2212','0cddf8b48c60a28511d51c008471106a.jpg','修空调，修电脑，重装系统，系统升级，修水表','110120119','hhhh',NULL);

